import React from "react";
import PropTypes from "prop-types";
import Card from "../Card";

export default function Board({ cards, flipped, handleClick, dimens, disables, solved }){
    return <div className="board">
        {cards.map((card) => (
        <Card 
            key={card.id}
            id={card.id}
            type={card.type}
            width={dimens/ 4.5}
            height={dimens/ 4.5}
            // backStyle={Card.backStyle}
            // frontStyle={Card.frontStyle}
            flipped={flipped.includes(card.id)}
            handleClick={handleClick(card.id)}
            disables={disables || solved.includes(card.id)}
            solved={solved.includes(card.id)}
        />
        ))}
    </div>
}

Board.propTypes = {
    dimens: PropTypes.number.isRequired,
    cards: PropTypes.arrayOf(PropTypes.shape({})).isRequired,
    flipped: PropTypes.arrayOf(PropTypes.number).isRequired,
    handleClick: PropTypes.func.isRequired,
    solved: PropTypes.arrayOf(PropTypes.number).isRequired,
    disables: PropTypes.bool.isRequired,

}
